const init = function () {
  screenWidth = window.innerWidth;
  let tempWidth = [];
  window.onresize = window.onload = function () {
    screenWidth = this.innerWidth;
    tempWidth.push(screenWidth);
    if (
      tempWidth.length > 2 &&
      tempWidth[tempWidth.length - 1] != tempWidth[tempWidth.length - 2]
    ) {
      location.reload();
      tempWidth.push(tempWidth[tempWidth.length - 1]);
    }
  };

  if (window.location.hash.substring(1, window.location.hash.length)) {
    country = window.location.hash.substring(1, window.location.hash.length);

    getCountriesJson(country);
  } else {
    getWorldData();
    getCountriesJson2();

    let today = new Date();
    if (document.querySelector(".js-today")) {
      document.querySelector(".js-today").innerHTML =
        dagen[today.getDay()] +
        " " +
        today.getDate() +
        " " +
        months[today.getMonth()] +
        " " +
        today.getFullYear();
    }
  }
};

document.addEventListener("DOMContentLoaded", init);


